    private void btnValidarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnValidarMouseClicked
        String cadena = jTextField1.getText();
        String tipoGramatica = jTextField2.getText();
        System.setOut(new PrintStream(new CapturaSalida(jTextArea1)));
        if (tipoGramatica.equals("1")) {
        Gramatica gramatica1 = new Gramatica(cadena);
        gramatica1.esValido();
    } else if (tipoGramatica.equals("2")) {
        GramaticaPrueba gramatica2 = new GramaticaPrueba(cadena);
        gramatica2.esValido();
    }
    
    }//GEN-LAST:event_btnValidarMouseClicked

